import styled from 'styled-components';

export const ButtonContainer = styled.button`
font-size: 1.5 rem;
background-color: ${props => props.cart? "aliceblue" : "cyan"};
border: 0.2rem solid darkcyan;
border-color: ${props => props.cart? "darkcyan" : "darkcyan"};
color: ${props => props.cart? "darkcyan" : "darkcyan"};
border-radius: 0.5rem;
padding: 0.2rem 0.5rem;
margin: 0.2rem 0.5rem 0.2rem 0;
transition: all 0.3s ease-in-out;
&:hover{
    color: ${props => props.cart? "aliceblue" : "cyan"};
    border-color: ${props => props.cart? "aliceblue" : "cyan"};
    background-color: ${props => props.cart? "darkcyan" : "darkcyan"};
}
&:focus{
    outline: none;
}
`;
