import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import Logo from '../logo.svg';
import styled from 'styled-components';

export default class Navbar extends Component {
    render() {
        return (
            <nav className="navbar navbar-light bg-info">
                <Link to='/'>
                    {/* <p className="navbar-brand">Riddle Store</p> */}
                    <img src={Logo} alt="Logo" width="40px" className="navbar-brand"/>
                </Link>
                <ul className="navbar-nav align-items-center">
                    <li className="nav-item ml-5"><Link to='/'>Products</Link></li>
                </ul>
                <Link to='/cart' className="ml-auto">
                    <ButtonContainer><i className="fa fa-cart-plus mr-2"></i>My Cart</ButtonContainer>
                </Link>
            </nav>
        )
    }
}

const ButtonContainer = styled.button`
font-size: 1.5 rem;
background-color: gold;
border: 0.2rem solid green;
color: green;
border-radius: 0.5rem;
padding: 0.2rem 0.5rem;
transition: all 0.3s ease-in-out;
&:hover{
    color: gold;
    border-color: gold;
    background-color: green;
}
&:focus{
    outline: none;
}
`;
